import { useState } from 'react';

const api = {
    key: "66aa88baf7e62908a9e83092f343247f",
    base: "https://api.openweathermap.org/data/2.5/weather?q="
}

function App() {

    const [query, setQuery] = useState('');
    const [weather, setWeather] = useState({});

    const search = evt => {
        if (evt.key === "Enter") {
            fetch(api.base + query + "&units=metric&APPID=" + api.key)
                .then(res => res.json())
                .then(result => {
                    setWeather(result);
                    setQuery('');
                    console.log(result);
                });
        }

    }

    const dateBulider = (d) => {
        let months = ["January", "Febraury", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
        let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];

        let day = days[d.getDay()];
        let date = d.getDate();
        let month = months[d.getMonth()];
        let year = d.getFullYear();
        return day + ", " + date + " " + month + " " + year;
    }
    // if(weather.weather[0].main == "Haze"){
