    // }
    // else if(weather.weather[0].main == "Clouds"){
    //     document.body.style.backgroundImage = "url('/src/clouds.jpg')";
    // }
    // else if(weather.weather[0].main == "Clear"){
    //     document.body.style.backgroundImage = "url('/src/clear.jpg')";
    // }
    // else if(weather.weather[0].main == "Smoke"){
    //     document.body.style.backgroundImage = "url('/src/smoke.jpg')";
    // }
    // else if(weather.weather[0].main == "Rain"){
    //     document.body.style.backgroundImage = "url('/src/rain.jpg')";
    // }
    // else if(weather.weather[0].main == "Mist"){
    //     document.body.style.backgroundImage = "url('/src/mist.jpg')";
    // }
    // else if(weather.weather[0].main == "Thunderstorm"){
    //     document.body.style.backgroundImage = "url('/src/thunderstorm.jpg')";
    // }
    // else if(weather.weather[0].main == "Snow"){
    //     document.body.style.backgroundImage = "url('/src/snow.jpg')";
    // }
    // else if(weather.weather[0].main == "Drizzle"){
    //     document.body.style.backgroundImage = "url('/src/drizzle.jpg')";
    // }




    return (
        < div className="app" >
            <main>
                <div class="heading">Weather App</div>
                <input type="text" autocomplete="off" placeholder="Search City Here" class="search-box" onChange={e => setQuery(e.target.value)} value ={query} onKeyPress={search}></input>
                <section class="location">
                    <div class="city">{weather.name}, {weather.sys.country}</div>
                </section>
                <div class="date">{dateBulider(new Date())}</div>
                <div class="information">
                    <div class="weather">Sunny</div>
                    <div class="temp"><span class="temps">15</span><span>°C</span></div>
                    <div class="min-max">
                        <div class="min-temp"><span>Min Temperature : </span><span id="min-temps">15</span><span class="cel">°C</span></div>
                        <div class="max-temp"><span>Max Temperature : </span><span id="max-temps">15</span><span class="cel">°C</span></div>
                    </div>
                </div>
            </main>
        </div>);

}

export default App;
